mapper.select:
 select 

 from cash_back_info


 		and cash_back_id = #{cashBackId,jdbcType=VARCHAR}


 		and member_id = #{memberId,jdbcType=VARCHAR}


 		and member_name like "%" #{memberName,jdbcType=VARCHAR} "%"



		limit #{pageNo,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select 
count(0)
from cash_back_info


		and cash_back_id = #{cashBackId,jdbcType=VARCHAR}


		and member_id = #{memberId,jdbcType=VARCHAR}


		and member_name like "%" #{memberName,jdbcType=VARCHAR} "%"
