mapper.select:
 select 

 from cash_back


 		and cash_remark like "%" #{cashRemark,jdbcType=VARCHAR} "%"


 		and cash_type = #{cashType,jdbcType=VARCHAR}


 		and cash_member = #{cashMember,jdbcType=VARCHAR}


 		and cash_member_name like "%" #{cashMemberName,jdbcType=VARCHAR} "%"


 order by create_time desc

		limit #{pageNo,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select count(0) from cash_back
and cash_remark like "%" #{cashRemark,jdbcType=VARCHAR} "%"
and cash_type = #{cashType,jdbcType=VARCHAR}
and cash_member = #{cashMember,jdbcType=VARCHAR}
and cash_member_name like "%" #{cashMemberName,jdbcType=VARCHAR} "%"
