mapper.select:
select

from goods_info

	and classify_name like "%" #{classifyName,jdbcType=VARCHAR} "%"
	and goods_name like "%" #{goodsName,jdbcType=VARCHAR} "%"
	and goods_status = #{goodsStatus,jdbcType=VARCHAR}
	and goods_type = #{goodsType,jdbcType=VARCHAR}
	and classify_id = #{classifyId,jdbcType=VARCHAR}
	and sifting = #{sifting,jdbcType=VARCHAR}


	order by ${pageSort}


	limit #{pageNo,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select count(0) from goods_info

	and classify_name like "%" #{classifyName,jdbcType=VARCHAR} "%"
	and goods_name like "%" #{goodsName,jdbcType=VARCHAR} "%"
	and goods_status = #{goodsStatus,jdbcType=VARCHAR}
	and goods_type = #{goodsType,jdbcType=VARCHAR}
	and sifting = #{sifting,jdbcType=VARCHAR}

mapper.update:
update goods_info
goods_name = #{goodsName,jdbcType=VARCHAR}, goods_img = #{goodsImg,jdbcType=VARCHAR}, goods_status = #{goodsStatus,jdbcType=VARCHAR}, goods_type = #{goodsType,jdbcType=VARCHAR}, goods_price = #{goodsPrice,jdbcType=DOUBLE}, red_packet = #{redPacket,jdbcType=INTEGER}, goods_details = #{goodsDetails,jdbcType=VARCHAR}, goods_param = #{goodsParam,jdbcType=VARCHAR}, express_fee = #{expressFee,jdbcType=DOUBLE}, inventory = #{inventory,jdbcType=INTEGER}, salesvolume = #{salesvolume,jdbcType=INTEGER}, buy_num = #{buyNum,jdbcType=INTEGER}, browse_num = #{browseNum,jdbcType=INTEGER}, sifting = #{sifting,jdbcType=VARCHAR}, classify_id = #{classifyId,jdbcType=VARCHAR}, classify_name = #{classifyName,jdbcType=VARCHAR}, pubdate = #{pubdate,jdbcType=BIGINT},
where goods_id = #{goodsId,jdbcType=VARCHAR}
