mapper.select:
select count(0) 

from order_info ord LEFT JOIN member_info mi ON mi.member_id = ord.member_id

	and ord.member_id = #{memberId,jdbcType=VARCHAR}
	and ord.order_code like "%" #{orderCode,jdbcType=VARCHAR} "%"
	and ord.goods_type = #{goodsType,jdbcType=VARCHAR}
	and ord.order_status = #{orderStatus,jdbcType=VARCHAR}
	and ord.order_status not in ('8','9','0')

	and mi.member_name like "%" #{memberName,jdbcType=VARCHAR} "%"
	and mi.cellphone like "%" #{cellphone,jdbcType=VARCHAR} "%"
